def evaluate_accuracy_in_classes(refs, pres, nbest, dataset):
    refs = [x.target.strip() for x in refs]
    pres = [x.strip() for x in pres]

    # print(nbest)
    # print(len(refs))
    # print(len(pres))
    # print(len(refs)*nbest)
    assert len(refs)*nbest == len(pres)

    length = len(refs)
    count = 0
    correct_1 = 0
    correct_5 = 0
    correct_10 = 0
    matched_indices = set()
    for i in range(length):
        r = refs[i]
        for j in range(nbest):
            p = pres[i*nbest+j]
            if r==p:
                count+=1
                matched_indices.add(i)
                if j<=0:
                    correct_1+=1
                if j<=4:
                    correct_5+=1
                if j<=10:
                    correct_10+=1
                break

    acc = round(count / length * 100, 3)
    acc_1 = round(correct_1 / length * 100, 3)
    acc_5 = round(correct_5 / length * 100, 3)
    acc_10 = round(correct_10 / length * 100, 3)

    print("nbest:", nbest)
    print("total instances:", length)
    print("exact match(j<=0):", correct_1, "acc:", acc_1)
    print("exact match(j<=4):", correct_5, "acc:", acc_5)
    print("exact match(j<=9):", correct_10, "acc:", acc_10)
    print("matched_indices: ", matched_indices)

    # matched_indices_filename = "/home/amllab-2070/Desktop/Thesis78-84/PLBART/scripts/code_to_code/CC_review_for_repair/files/PLBART_matched_indices.txt"
    # with open(matched_indices_filename, "w") as f:
    #     f.write('\n'.join(str(idx) for idx in matched_indices))

    # statusCount(generate_test_ara(), matched_indices)

    test_data_stat = None
    if 'tufano' in dataset:
        test_data_stat = tufano_test_data_stat
    else:
        test_data_stat = R4R_test_data_stat

    insert_count = 0
    delete_count = 0
    update_count = 0
    for i in matched_indices:
        if i in test_data_stat["insert"]:
            insert_count += 1
        elif i in test_data_stat["delete"]:
            delete_count += 1
        elif i in test_data_stat["update"]:
            update_count += 1

    print(f'total_insert samples: {len(test_data_stat["insert"])}\t success_count: {insert_count}\t percentage: {insert_count/len(test_data_stat["insert"])*100}')
    print(f'total_delete_samples: {len(test_data_stat["delete"])}\t success_count: {delete_count}\t percentage: {delete_count/len(test_data_stat["delete"])*100}')
    print(f'total_update_samples: {len(test_data_stat["update"])}\t success_count: {update_count}\t percentage: {update_count/len(test_data_stat["update"])*100}')
